INSERT INTO `wp_snippets` VALUES (6,'[REST API] Add post type links','','add_action(\r\n  \'rest_api_init\',\r\n  function () {\r\n    // Get all registered post types.\r\n    $post_types = get_post_types( array( \'show_in_rest\' => true ), \'objects\' );\r\n    \r\n    foreach ( $post_types as $post_type ) {\r\n      // Embed type links only for \'post\' or post types with archive.\r\n      if ( \'post\' === $post_type->name || $post_type->has_archive ) {\r\n        add_filter(\r\n          \"rest_prepare_{$post_type->name}\",\r\n          function ( $response ) {\r\n            // Generate the URL for the type object.\r\n            $type      = $response->data[\'type\'];\r\n            $types_url = rest_url( \"wp/v2/types/$type\" );\r\n        \r\n            // Add a link called `type`.\r\n            $response->add_links(\r\n              array(\r\n                \'type\' => array(\r\n                  \'href\'       => $types_url,\r\n                  \'embeddable\' => true,\r\n                ),\r\n              )\r\n            );\r\n            return $response;\r\n          }\r\n        );\r\n      }\r\n    }\r\n  },\r\n  50\r\n);','','front-end',10,1,'2020-09-01 15:57:03');
